import React from "react";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import { Link, useLocation } from "react-router-dom";

const Header = () => {
  const location = useLocation(); // to detect current path

  // helper function for active link styling
  const isActive = (path) => location.pathname === path;

  return (
    <Navbar
      expand="lg"
      bg="white"
      className="shadow-sm py-3 sticky-top border-bottom"
    >
      <Container>
        {/* ==== Logo / Brand ==== */}
        <Navbar.Brand
          as={Link}
          to="/"
          className="fw-bold fs-4 text-primary d-flex align-items-center"
        >
          <img
            src="https://cdn-icons-png.flaticon.com/512/809/809957.png"
            alt="logo"
            width="35"
            height="35"
            className="me-2"
          />
          RealEstate<span className="text-dark">Hub</span>
        </Navbar.Brand>

        {/* ==== Toggle for mobile ==== */}
        <Navbar.Toggle aria-controls="navbar-nav" />

        {/* ==== Navbar Links ==== */}
        <Navbar.Collapse id="navbar-nav">
          <Nav className="ms-auto align-items-lg-center">
            <Nav.Link
              as={Link}
              to="/"
              className={`mx-2 fw-semibold ${
                isActive("/") ? "text-primary" : "text-dark"
              }`}
            >
              Home
            </Nav.Link>

            <Nav.Link
              as={Link}
              to="/plots"
              className={`mx-2 fw-semibold ${
                isActive("/plots") ? "text-primary" : "text-dark"
              }`}
            >
              Plots
            </Nav.Link>

            <Nav.Link
              as={Link}
              to="/contact"
              className={`mx-2 fw-semibold ${
                isActive("/contact") ? "text-primary" : "text-dark"
              }`}
            >
              Contact
            </Nav.Link>

            {/* CTA Button */}
            <Button
              as={Link}
              to="/contact"
              variant="primary"
              className="ms-lg-3 mt-3 mt-lg-0 fw-semibold px-3"
            >
              Enquire Now
            </Button>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
