// src/Components/ServiceCard/ServiceCard.jsx
import React from "react";
// REMOVED: import { PRIMARY_COLOR } from "../../utils/constants";

// --- Local Hardcoded Constant ---
const MANUAL_PRIMARY_COLOR = "#00A896";
// ---------------------------------

// import "./ServiceCard.css"; // Optional: for custom styles

const ServiceCard = ({ iconClass, title, description }) => (
  <div className="col-md-4 mb-4">
    <div
      className="text-center p-4 h-100 rounded-4 shadow-sm"
      style={{
        backgroundColor: "white",
        // Replaced PRIMARY_COLOR
        border: `1px solid ${MANUAL_PRIMARY_COLOR}30`,
      }}
    >
      <i
        className={`bi ${iconClass} display-4 mb-3`}
        // Replaced PRIMARY_COLOR
        style={{ color: MANUAL_PRIMARY_COLOR }}
      ></i>
      <h5
        className="fw-bold"
        // Replaced PRIMARY_COLOR
        style={{ color: MANUAL_PRIMARY_COLOR }}
      >
        {title}
      </h5>
      <p className="text-muted">{description}</p>
    </div>
  </div>
);

export default ServiceCard;
